mapper.update:
update c_activity
set
owner = #{owner}, name = #{name}, startDate = #{startDate}, endDate = #{endDate}, cost = #{cost}, description = #{description}, editTime = #{editTime}, editBy = #{editBy}
where id = #{id}

mapper.delete:
delete from c_activity

where id in


    #{id}

mapper.select:
select count(*)
from c_activity a join c_user u on a.owner = u.id
and a.name like concat('%', #{name}, '%')
and u.name like concat('%', #{owner} ,'%')
and a.startDate > #{startDate}
and a.endDate < #{endDate}

select a.id, a.name, u.name as owner, a.startDate, a.endDate
from c_activity a join c_user u on a.owner = u.id
and a.name like concat('%', #{name}, '%')
and u.name like concat('%', #{owner} ,'%')
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc
limit #{skipCount}, #{pageSize}
