mapper.update:
update c_trade
set
owner = #{owner}, amount = #{amount}, name = #{name}, expectedTradeDate = #{expectedTradeDate}, stage = #{stage}, type = #{type}, source = #{source}, editBy = #{editBy}, editTime = #{editTime}, description = #{description}, contactMinute = #{contactMinute}, nextContactTime = #{nextContactTime}, customerId = #{customerId}, activityId = #{activityId}, contactId = #{contactId}
where id = #{id}
